trait A {\n      public function methodA() {\n          echo "A";\n      }\n  }\n\n  trait B {\n      public function methodB() {\n          echo "B";\n      }\n  }\n\n  class C {\n      use A, B;\n\n      public function methodC() {\n          echo "C";\n      }\n  }\n\n  $c = new C();\n  $c->methodA();  // Output: A\n  $c->methodB();  // Output: B\n  $c->methodC();  // Output: C