// First query\n$query1 = "SELECT * FROM tblProducts WHERE ProductId ='$SCId'";\n$rsPrimary1 = mysql_query($query1) or die ("Query '$query1' failed with error message: \"" . mysql_error() . '"');\n\n// Second query\n$query2 = "SELECT * FROM tblProducts WHERE Cat ='$CatType' AND Type ='$TypeType'";\n$rsPrimary2 = mysql_query($query2) or die ("Query '$query2' failed with error message: \"" . mysql_error() . '"');\n\n// Counting number of rows for the first query\n$num1 = mysql_numrows($rsPrimary1);\n\n// Counting number of rows for the second query\n$num2 = mysql_numrows($rsPrimary2);\n\nmysql_close();