<?php\nsession_start();\n\ndefine('MAX_LOGIN_ATTEMPTS', 3);\ndefine('LOCKOUT_TIME', 600); // 10 minutes in seconds\n\nfunction check_login($username, $password) {\n    // Replace this with actual authentication logic\n    return $username === 'user' && $password === 'pass';\n}\n\nif (!isset($_SESSION['login_attempts'])) {\n    $_SESSION['login_attempts'] = 0;\n    $_SESSION['last_login_attempt'] = time();\n}\n\nif ($_SESSION['login_attempts'] >= MAX_LOGIN_ATTEMPTS && time() - $_SESSION['last_login_attempt'] < LOCKOUT_TIME) {\n    echo "You have been locked out due to too many failed login attempts. Please try again later.";\n} else {\n    if (isset($_POST['username']) && isset($_POST['password'])) {\n        if (check_login($_POST['username'], $_POST['password'])) {\n            echo "Login successful!";\n            $_SESSION['login_attempts'] = 0; // Reset on successful login\n        } else {\n            $_SESSION['login_attempts']++;\n            $_SESSION['last_login_attempt'] = time();\n            echo "Invalid username or password. You have " . (MAX_LOGIN_ATTEMPTS - $_SESSION['login_attempts']) . " attempts left.";\n            \n            if ($_SESSION['login_attempts'] >= MAX_LOGIN_ATTEMPTS) {\n                echo " You have been locked out. Please try again in 10 minutes.";\n            }\n        }\n    }\n}\n?>\n\n<form method="post">\n    <input type="text" name="username" placeholder="Username" required>\n    <input type="password" name="password" placeholder="Password" required>\n    <button type="submit">Login</button>\n</form>