$mysqli = new mysqli("host", "user", "password", "database");\n\n// Check connection\nif ($mysqli -> connect_errno) {\n  die("Failed to connect to MySQL: " . $mysqli -> connect_error);\n}\n\n// First query\n$query1 = "SELECT * FROM tblProducts WHERE ProductId ='$SCId'";\n$rsPrimary1 = $mysqli->query($query1);\n\nif (!$rsPrimary1) {\n  die("Query '$query1' failed with error message: \"" . $mysqli->error . '"');\n}\n\n// Second query\n$query2 = "SELECT * FROM tblProducts WHERE Cat ='$CatType' AND Type ='$TypeType'";\n$rsPrimary2 = $mysqli->query($query2);\n\nif (!$rsPrimary2) {\n  die("Query '$query2' failed with error message: \"" . $mysqli->error . '"');\n}\n\n// Counting number of rows for the first query\n$num1 = $rsPrimary1->num_rows;\n\n// Counting number of rows for the second query\n$num2 = $rsPrimary2->num_rows;\n\n$mysqli->close();