function sortByCategoryAndDate($data) {\n    // Group data by category_name\n    $grouped = [];\n    foreach ($data as $item) {\n        $category = $item['category_name'];\n        if (!isset($grouped[$category])) {\n            $grouped[$category] = [];\n        }\n        $grouped[$category][] = $item;\n    }\n\n    // Sort each category by link_updated in descending order\n    foreach ($grouped as &$items) {\n        usort($items, function($a, $b) {\n            return strtotime($b['link_updated']) - strtotime($a['link_updated']);\n        });\n        // Limit to 5 items per category\n        $items = array_slice($items, 0, 5);\n    }\n\n    return $grouped;\n}\n\n// Example usage\n$data = [\n    ["link_id" => "20", "link_url" => "http://url.of/website", "link_name" => "Website title", "link_target" => "", "link_description" => "", "link_updated" => "2009-05-24 16:51:04", "taxonomy_id" => "36", "term_id" => "34", "category_name" => "First category"],\n    ["link_id" => "21", "link_url" => "http://url.of/website2", "link_name" => "Website title 2", "link_target" => "", "link_description" => "", "link_updated" => "2009-05-24 16:51:24", "taxonomy_id" => "36", "term_id" => "34", "category_name" => "First category"],\n    ["link_id" => "22", "link_url" => "http://url.of/website3", "link_name" => "Older link", "link_target" => "", "link_description" => "", "link_updated" => "2009-05-20 05:32:56", "taxonomy_id" => "36", "term_id" => "34", "category_name" => "Second category"],\n    // Add more data as needed\n];\n\n$result = sortByCategoryAndDate($data);\nprint_r($result);