class DerivedClass {\n    public $internal_id;\n    public $external_id;\n\n    public function __construct($internalId, $externalId) {\n        $this->internal_id = $internalId;\n        $this->external_id = $externalId;\n    }\n}\n\n$WSDL = 'http://example.com/service?wsdl';\n$soapSettings = array(\n    'trace' => 1,\n    'exceptions' => true,\n);\n\ntry {\n    $client = new SoapClient($WSDL, $soapSettings);\n\n    $obj = new DerivedClass($internalId, $externalId);\n\n    $params = array(\n        'obj' => new SoapVar($obj, SOAP_ENC_OBJECT, 'DerivedClass', 'http://namespace.of.your.service')\n    );\n\n    $response = $client->webmethod($params);\n\n    print_r($response);\n} catch (SoapFault $fault) {\n    echo 'Error: ' . $fault->getMessage();\n}