<?php\n// Determine the environment\nif (gethostbyaddr('127.0.0.1') == 'hello.sabya') {\n    define('ENVIRONMENT', 'production');\n} else {\n    define('ENVIRONMENT', 'development');\n}\n\n// Load the appropriate configuration file based on the environment\nswitch (ENVIRONMENT) {\n    case 'production':\n        require_once APPPATH . 'config/config_production.php';\n        break;\n    case 'development':\n        require_once APPPATH . 'config/config_development.php';\n        break;\n    default:\n        exit('The application environment is not set correctly.');\n}\n?>