// Load the Feed helper\n$feed = new Feed();\n\n// Define your feed items\n$items = array(\n    array(\n        'title' => 'First Item',\n        'link'  => 'http://example.com/first-item',\n        'description' => 'Description of the first item',\n        'pubDate' => 'Tue, 10 Oct 2023 14:00:00 +0000',\n    ),\n    array(\n        'title' => 'Second Item',\n        'link'  => 'http://example.com/second-item',\n        'description' => 'Description of the second item',\n        'pubDate' => 'Wed, 11 Oct 2023 14:00:00 +0000',\n    ),\n    // Add more items as needed\n);\n\n// Define the feed information\n$info = array(\n    'title'       => 'My RSS Feed',\n    'link'        => 'http://example.com',\n    'description' => 'Description of my RSS feed',\n    'language'    => 'en-us',\n    'pubDate'     => date(DATE_RSS, strtotime('now')),\n);\n\n// Create the RSS feed\n$rss_feed = $feed->create($info, $items);\n\n// Output the RSS feed\nheader('Content-Type: application/rss+xml');\necho $rss_feed;