function verifyRegistrationKey($username, $key) {\n    $user = JFactory::getUser($username);\n    if ($user->id) {\n        $db = JFactory::getDbo();\n        $query = $db->getQuery(true)\n            ->select($db->quoteName('registration_key'))\n            ->from($db->quoteName('#__registration_keys'))\n            ->where($db->quoteName('user_id') . ' = ' . $db->quote($user->id))\n            ->where($db->quoteName('registration_key') . ' = ' . $db->quote($key));\n        \n        $db->setQuery($query);\n        $result = $db->loadResult();\n        \n        return $result ? true : false;\n    }\n    return false;\n}\n\n// Example usage\n$isValid = verifyRegistrationKey('exampleUser', 'provided-key');\nif ($isValid) {\n    echo "Key is valid!";\n} else {\n    echo "Invalid key!";\n}