<?php\n$fileName = $_GET['textFile'];\n$filePath = 'text/' . $fileName . '.txt';\n\nif (file_exists($filePath)) {\n    $fp = fopen($filePath, 'r');\n    $pos = -2;\n    $char = '';\n\n    while ($char != "\n") {\n        fseek($fp, $pos, SEEK_END);\n        $char = fgetc($fp);\n        $pos--;\n    }\n\n    $lastLine = fgets($fp);\n    fclose($fp);\n    echo $lastLine;\n} else {\n    echo 0;\n}\n?>