$path = "c:/xampp/htdocs/";\n$word = null;\n\ntry {\n    com_load_typelib('Word.Application');\n    $word = new COM("word.application") or die("Unable to instantiate Word");\n\n    // Make Word visible (for debugging purposes)\n    $word->Visible = 0;\n\n    // Open the document\n    $word->Documents->Open($path . "tmp/invoice.doc");\n\n    // Edit the document\n    $bookmarkname = "InvoiceNo";\n    if($word->ActiveDocument->Bookmarks->Exists($bookmarkname)) {\n        $word->ActiveDocument->Bookmarks($bookmarkname)->Range->Text = "INVOICE ID";\n    } else {\n        throw new Exception("Bookmark not found in document.");\n    }\n\n    // Save the document\n    $word->Documents[1]->SaveAs($path . "tmp/invoice2.doc");\n\n    // Close the document\n    $word->ActiveDocument->Close(false);\n    $word->Quit();\n\n} catch (Exception $e) {\n    echo "An error occurred: " . $e->getMessage();\n} finally {\n    if ($word !== null) {\n        $word->Quit();\n    }\n    $word = null;\n}