// Initialize ACL\n$acl = new Zend_Acl();\n\n// Define roles\n$acl->addRole(new Zend_Acl_Role('guest'));\n$acl->addRole(new Zend_Acl_Role('editor'), 'guest'); // editor inherits from guest\n$acl->addRole(new Zend_Acl_Role('admin'));\n\n// Define resource types\n$acl->add(new Zend_Acl_Resource('post'));\n$acl->add(new Zend_Acl_Resource('gallery'));\n\n// Define specific resource instances\n$acl->add(new Zend_Acl_Resource('post_5'), 'post'); // post_5 is a child of post\n$acl->add(new Zend_Acl_Resource('gallery_1'), 'gallery'); // gallery_1 is a child of gallery\n\n// Define permissions for roles\n$acl->allow('guest', null, 'read'); // guest can read all resources\n$acl->allow('editor', 'post', 'edit'); // editor can edit all posts\n$acl->allow('admin'); // admin can do everything\n\n// Define specific permissions for instances\n$acl->allow('editor', 'post_5', 'edit'); // editor can edit post ID #5\n\n// Check permissions\nif ($acl->isAllowed('guest', 'post', 'read')) {\n    echo "Guest can read posts.";\n}\n\nif ($acl->isAllowed('editor', 'post_5', 'edit')) {\n    echo "Editor can edit post ID #5.";\n}