$cOption = isset($_GET['fo']) ? (int)$_GET['fo'] : 0;\n\nswitch($cOption) {\n    case 1:\n        $recordsPerPage = 5;\n        $pageNum = isset($_GET['pg']) ? (int)$_GET['pg'] : 1;\n        $offset = ($pageNum - 1) * $recordsPerPage;\n\n        $connection = new mysqli("host", "username", "password", "database");\n\n        if ($connection->connect_error) {\n            die("Connection failed: " . $connection->connect_error);\n        }\n\n        $onlineresult = $connection->query("SELECT * FROM online");\n\n        if ($onlineresult) {\n            while ($ousers = $onlineresult->fetch_assoc()) {\n                $onuid = $ousers['uid'];\n\n                $flist = $connection->query("SELECT * FROM friends_list WHERE fid='$onuid' AND uid='$myid' LIMIT $offset, $recordsPerPage");\n\n                if ($flist) {\n                    while ($fri = $flist->fetch_assoc()) {\n                        $id = $fri['id'];\n                        $uid = $fri['uid'];\n                        $fid = $fri['fid'];\n\n                        $userinfomation = $connection->query("SELECT * FROM accounts WHERE uid='$fid'");\n                        // Process the user information\n                    }\n                } else {\n                    echo "Error: " . $connection->error;\n                }\n            }\n        } else {\n            echo "Error: " . $connection->error;\n        }\n\n        $connection->close();\n        break;\n    // Other cases...\n}