<?php\n\nfunction double_metaphone($string) {\n    // Implementation of the Double Metaphone algorithm\n    // This is a simplified version and may not cover all edge cases.\n    // For a complete implementation, you might want to use a library.\n    \n    $string = strtoupper($string);\n    $primary = '';\n    $secondary = '';\n    \n    $length = strlen($string);\n    $current = 0;\n\n    while ($current < $length) {\n        $char = $string[$current];\n        \n        switch ($char) {\n            case 'B':\n                $primary .= 'B';\n                $secondary .= 'B';\n                if ($string[$current + 1] == 'B') $current++;\n                break;\n            case 'C':\n                if ($string[$current + 1] == 'H') {\n                    $primary .= 'X';\n                    $secondary .= 'X';\n                    $current++;\n                } else {\n                    $primary .= 'K';\n                    $secondary .= 'K';\n                }\n                break;\n            // Add more rules here...\n            default:\n                $primary .= $char;\n                $secondary .= $char;\n        }\n        \n        $current++;\n    }\n\n    return [$primary, $secondary];\n}\n\nfunction do_words_rhyme($word1, $word2) {\n    list($primary1, $secondary1) = double_metaphone($word1);\n    list($primary2, $secondary2) = double_metaphone($word2);\n\n    return ($primary1 == $primary2 || $secondary1 == $secondary2);\n}\n\n// Example usage\n$word1 = "write";\n$word2 = "right";\n\nif (do_words_rhyme($word1, $word2)) {\n    echo "$word1 and $word2 rhyme!";\n} else {\n    echo "$word1 and $word2 do not rhyme.";\n}\n?>