require 'vendor/autoload.php';\n\nuse PHPExcel;\nuse PHPExcel_IOFactory;\n\n$xls = new PHPExcel();\n$sheet = $xls->getActiveSheet()->setTitle($name);\n\n$row = 0;\nforeach ($rec as $field) {\n    $col = 0;\n    $data = ($field['data'] ? $field['data'] : $field);\n    $sheet->setCellValueByColumnAndRow($col++, $row, $data);\n    $row++;\n}\n\n// Save the file as Excel 2007 format\n$writer = PHPExcel_IOFactory::createWriter($xls, 'Excel2007');\n$writer->save('output.xlsx');