abstract class AbstractClass {\n    \n    // Mapping of strings to their respective superglobals\n    private static $superglobals = [\n        'get' => $_GET,\n        'post' => $_POST,\n        'request' => $_REQUEST,\n        // Add more superglobals as needed\n    ];\n\n    // Method to get the value from the specified superglobal\n    public static function get($key, $method = 'get') {\n        return self::fetchData($key, $method);\n    }\n\n    // Method to fetch data from the appropriate superglobal\n    private static function fetchData($key, $method) {\n        if (isset(self::$superglobals[$method][$key])) {\n            return self::$superglobals[$method][$key];\n        }\n        return false; // or null, or any default value as needed\n    }\n}\n\n// Example usage\n$id = AbstractClass::get('id', 'get');\necho $id;