class BinsController extends AppController {\n\n    public function updateOrder($binId, $videoOrder) {\n        foreach ($videoOrder as $order => $videoId) {\n            $this->BinsVideo->updateAll(\n                array('BinsVideo.order' => $order),\n                array(\n                    'BinsVideo.bin_id' => $binId,\n                    'BinsVideo.video_id' => $videoId\n                )\n            );\n        }\n    }\n}