class Sql {\n    public function execute() {\n        return "Executing SQL query";\n    }\n}\n\nclass Db {\n    private $chesterx;\n\n    public function __construct($chesterx) {\n        $this->chesterx = $chesterx;\n    }\n\n    public function query() {\n        // Access Sql class through chesterx\n        return $this->chesterx->getSql()->execute();\n    }\n}\n\nclass Chesterx {\n    private $db;\n    private $sql;\n\n    public function __construct() {\n        $this->sql = new Sql();\n        $this->db = new Db($this);\n    }\n\n    public function getDb() {\n        return $this->db;\n    }\n\n    public function getSql() {\n        return $this->sql;\n    }\n}\n\n$chesterx = new Chesterx();\necho $chesterx->getDb()->query(); // Outputs: Executing SQL query