<?php\nfunction convertToNormalCase($input) {\n    $input = strtolower($input); // Convert the entire string to lower case\n    $sentences = preg_split('/(\.|\?|\!)\s*/', $input, -1, PREG_SPLIT_DELIM_CAPTURE);\n    \n    $output = '';\n    foreach ($sentences as $key => $sentence) {\n        if ($key % 2 == 0) {\n            $output .= ucfirst(trim($sentence));\n        } else {\n            $output .= $sentence . ' ';\n        }\n    }\n    \n    return trim($output);\n}\n\n$input = "THIS IS AN EXAMPLE. THIS IS ANOTHER EXAMPLE! IS THIS WORKING?";\necho convertToNormalCase($input);\n?>