<?php\nfunction getNthWeekdayOfMonth($date) {\n    // Create DateTime object from the input date\n    $dateTime = new DateTime($date);\n    \n    // Get the day of the month\n    $day = $dateTime->format('j');\n    \n    // Get the weekday of the date (1 = Monday, 7 = Sunday)\n    $weekday = $dateTime->format('N');\n    \n    // Calculate the nth occurrence of the weekday\n    $nth = ceil($day / 7);\n    \n    return "{$nth}";\n}\n\n// Example usage:\n$date = '2009-06-12'; // June 12, 2009\n$nthWeekday = getNthWeekdayOfMonth($date);\necho "The date {$date} is the {$nthWeekday} " . $dateTime->format('l') . " of the month.";\n?>