<?php\n$lock_file = '/path_to_file/convert.lock';\n\nif (file_exists($lock_file)) {\n    // If the lock file exists, it means another instance is already running\n    exit;\n} else {\n    // Create a lock file to indicate that this instance is running\n    file_put_contents($lock_file, getmypid());\n}\n\nignore_user_abort(true);\n\ntry {\n    // Your video conversion logic here\n} finally {\n    // Remove the lock file when the script is done\n    unlink($lock_file);\n}\n?>