<?php\n// Create a new DOMDocument\n$doc = new DOMDocument('1.0', 'UTF-8');\n\n// Create the <Cell> element\n$cell = $doc->createElement('Cell');\n\n// Create the <Data> element\n$data = $doc->createElement('Data');\n\n// Create a text node with the desired content, using a literal string for the newline character\n$textContent = $doc->createTextNode('text' . '&#10;' . 'text2' . '&#10;');\n\n// Append the text node to the <Data> element\n$data->appendChild($textContent);\n\n// Append the <Data> element to the <Cell> element\n$cell->appendChild($data);\n\n// Append the <Cell> element to the document\n$doc->appendChild($cell);\n\n// Print the XML content\necho $doc->saveXML();\n?>