<?php\n// Run the .bat file and capture the output\n$output = shell_exec('cmd /c C:\\dir\\file.bat');\n\n// Check the output\nif ($output === null) {\n    die('Failed to execute .bat file');\n}\n\n// Assuming the output contains the server ID in a specific format\n// For example, if the output is:\n// "Server started with ID: i-1234567890abcdef"\n\n// Use a regular expression to extract the server ID\nif (preg_match('/Server started with ID: (i-\w+)/', $output, $matches)) {\n    $serverId = $matches[1];\n    \n    // Now you can use the server ID in your script\n    echo "Server ID: " . $serverId;\n    \n    // Example of running another .bat file with the extracted server ID\n    $associateCommand = "ec2-associate-address -i " . $serverId . " ip.###.###.###";\n    shell_exec('cmd /c ' . $associateCommand);\n} else {\n    die('Server ID not found in the output');\n}\n?>