<?php\n\nclass Default_Model_ProjectCategories extends Zend_Db_Table_Abstract {\n    protected $_name = 'categories';\n    protected $_dependentTables = array('Default_Model_Projects');\n\n    public function findProjects($id) {\n        $projectsTable = new Default_Model_Projects();\n        return $projectsTable->fetchAll($projectsTable->select()->where('cid = ?', $id));\n    }\n}