// Example usage of HybridAuth\n   require_once('hybridauth/hybridauth/Hybrid/Auth.php');\n   $config = array(\n       "base_url" => "path_to_hybridauth/",\n       "providers" => array (\n           "Google" => array (\n               "enabled" => true,\n               "keys"    => array ( "id" => "YOUR_GOOGLE_CLIENT_ID", "secret" => "YOUR_GOOGLE_CLIENT_SECRET" ),\n           ),\n           "Facebook" => array (\n               "enabled" => true,\n               "keys"    => array ( "id" => "YOUR_FACEBOOK_APP_ID", "secret" => "YOUR_FACEBOOK_APP_SECRET" ),\n           ),\n           // Add other providers as needed\n       )\n   );\n   $hybridauth = new Hybrid_Auth( $config );\n   $adapter = $hybridauth->authenticate( "Google" );\n   $contacts = $adapter->getUserContacts();