require 'vendor/autoload.php';\n\nuse Aws\S3\S3Client;\nuse Aws\Exception\AwsException;\n\n$s3 = new S3Client([\n    'region'  => 'your-region',\n    'version' => 'latest',\n    'credentials' => [\n        'key'    => 'your-key',\n        'secret' => 'your-secret',\n    ]\n]);\n\n$bucket = 'your-bucket';\n$key = 'your-key';\n$sourceFile = '/path/to/your/video/file.mp4';\n\ntry {\n    // Put the object in the S3 bucket\n    $result = $s3->putObject([\n        'Bucket' => $bucket,\n        'Key'    => $key,\n        'SourceFile' => $sourceFile,\n        'ACL'    => 'public-read'\n    ]);\n\n    // Wait until the file is uploaded\n    $s3->waitUntil('ObjectExists', [\n        'Bucket' => $bucket,\n        'Key' => $key\n    ]);\n\n    // File successfully uploaded, proceed to call EC2 instance\n    $curl = curl_init();\n    curl_setopt($curl, CURLOPT_URL, "http://your-ec2-instance-url");\n    curl_setopt($curl, CURLOPT_POST, 1);\n    curl_setopt($curl, CURLOPT_POSTFIELDS, "key=$key&bucket=$bucket");\n    curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);\n    $response = curl_exec($curl);\n    curl_close($curl);\n\n    echo "EC2 Response: " . $response;\n\n} catch (AwsException $e) {\n    // Output error message if fails\n    echo "Error uploading to S3: " . $e->getMessage();\n}