<?php\n// Check if the image parameter is set\nif (isset($_GET['image'])) {\n    $image = $_GET['image'];\n    \n    // Set the path to the image file\n    $imagePath = 'path/to/your/images/directory/' . $image;\n\n    // Check if the file exists and is a valid image\n    if (file_exists($imagePath) && exif_imagetype($imagePath)) {\n        // Get the image MIME type\n        $imageInfo = getimagesize($imagePath);\n        $mimeType = $imageInfo['mime'];\n\n        // Set the appropriate header for the image\n        header('Content-Type: ' . $mimeType);\n\n        // Read and output the image file\n        readfile($imagePath);\n    } else {\n        // Send a 404 response code if the image is not found\n        header('HTTP/1.0 404 Not Found');\n        echo 'Image not found.';\n    }\n} else {\n    // Send a 400 response code if the image parameter is missing\n    header('HTTP/1.0 400 Bad Request');\n    echo 'No image specified.';\n}\n?>