function sort_data_by_year(&$data, $year_index) {\n    $args = array();\n    \n    // Add the year to sort by first\n    $args[] = $data[$year_index];\n    $args[] = SORT_ASC;\n    \n    // Add all other years' arrays\n    foreach ($data as $index => $array) {\n        if ($index != $year_index) {\n            $args[] = &$data[$index];\n        }\n    }\n\n    // Call array_multisort with dynamic arguments\n    call_user_func_array('array_multisort', $args);\n}\n\n// Example usage\n$data = array(\n    array(0, 1, 2, 3, 4, 5, 6, 7),\n    array(6, 5, 4, 3, 6, 7, 8, 9),\n    array(1, 4, 2, 5, 7, 3, 1, 4),\n);\n\n$year_to_sort_by = 1; // Sort by the second year (index 1)\nsort_data_by_year($data, $year_to_sort_by);\n\nprint_r($data);