# build query to fetch search results\n$c = new Criteria();\n\n# set minimum price\nif ($request->getPostParameter('price_from') > 0) {\n    $criterion = $c->getNewCriterion(HomeModelPeer::PRICE, $request->getPostParameter('price_from'), Criteria::GREATER_EQUAL);\n}\n\n# set maximum price\nif ($request->getPostParameter('price_to') > 0) {\n    $maxCriterion = $c->getNewCriterion(HomeModelPeer::PRICE, $request->getPostParameter('price_to'), Criteria::LESS_EQUAL);\n    if (isset($criterion)) {\n        $criterion->addAnd($maxCriterion);\n    } else {\n        $criterion = $maxCriterion;\n    }\n}\n\n# add the combined criterion to the Criteria object\nif (isset($criterion)) {\n    $c->add($criterion);\n}