<?php\n// Assuming $con is your database connection\n$con = new mysqli('hostname', 'username', 'password', 'database_name');\n\n// Check connection\nif ($con->connect_error) {\n    die("Connection failed: " . $con->connect_error);\n}\n\n// Prepare the SQL statement\n$stmt = $con->prepare("UPDATE blpublication SET JournalName = ?, AcceptanceDate = ?, PublishedDate = ?, Comment = ? WHERE JobNo = ?");\n\n// Bind parameters to the SQL statement\n$stmt->bind_param('ssssi', $_POST['journal'], $_POST['acceptancedate'], $_POST['publisheddate'], $_POST['comment'], $_POST['jobno']);\n\n// Execute the statement\nif ($stmt->execute()) {\n    echo "Record updated";\n} else {\n    echo "Error: " . $stmt->error;\n}\n\n// Close the statement and connection\n$stmt->close();\n$con->close();\n?>