<?php\n$to = "recipient@example.com"; // Specify the recipient's email address\n$subject = "My subject"; // Define the email subject\n$message = "Hello world!"; // Define the email message\n$headers = "From: webmaster@example.com" . "\r\n" . // Define the sender's email address\n           "CC: another@example.com"; // Optionally, define CC recipients\n\n// Send email\nif(mail($to, $subject, $message, $headers)) {\n    echo "Email sent successfully.";\n} else {\n    echo "Email sending failed.";\n}\n?>