$a = 5;         // integer\n$b = "5";       // string\n\n// Equality comparison\nif ($a == $b) {\n    echo "a is equal to b\n";  // This will be true because the values are the same after type conversion.\n}\n\n// Identity comparison\nif ($a === $b) {\n    echo "a is identical to b\n";  // This will not be true because the types are different (integer vs string).\n} else {\n    echo "a is not identical to b\n";  // This will be executed.\n}