$userInput = [\n    'attribute1' => true,\n    'attribute2' => true,\n    'attribute3' => false,\n    // Add user inputs up to attribute40\n    'attribute40' => true\n];\n\n$sql = "SELECT id, name, \n            (IF(attribute1 = :attribute1, 1, 0) + \n             IF(attribute2 = :attribute2, 1, 0) + \n             IF(attribute3 = :attribute3, 1, 0) + \n             -- Add conditions up to attribute40\n             IF(attribute40 = :attribute40, 1, 0)) AS match_count \n        FROM items \n        ORDER BY match_count DESC \n        LIMIT 1";\n\n$stmt = $pdo->prepare($sql);\n\n// Bind parameters dynamically\nforeach ($userInput as $attribute => $value) {\n    $stmt->bindValue(":$attribute", $value, PDO::PARAM_BOOL);\n}\n\n$stmt->execute();\n$bestMatch = $stmt->fetch(PDO::FETCH_ASSOC);\n\nprint_r($bestMatch);