// Collect values from the dropdowns\n$year = $_POST['Year'];\n$month = $_POST['Month'];\n$day = $_POST['Day'];\n$hour = $_POST['Hour'];\n$minute = $_POST['Minute'];\n\n// Combine them into a datetime string\n$dateTime = "$year-$month-$day $hour:$minute:00";\n\n// Create a new DateTime object to ensure the format is correct\n$date = DateTime::createFromFormat('Y-m-d H:i:s', $dateTime);\n$formattedDate = $date->format('Y-m-d H:i:s');\n\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Prepare and bind\n$stmt = $conn->prepare("INSERT INTO sessions (Location1, Location2, Date) VALUES (?, ?, ?)");\n$stmt->bind_param("sss", $location1, $location2, $formattedDate);\n\n// Set parameters and execute\n$location1 = $_POST['Location1'];\n$location2 = $_POST['Location2'];\n$stmt->execute();\n\necho "New record created successfully";\n\n$stmt->close();\n$conn->close();