<?php\n// Enable mbstring extension\nmb_internal_encoding("UTF-8");\n\n// Use mbstring functions for multibyte-safe string operations\n$str = "こんにちは"; // "Hello" in Japanese\n\n// Get the length of the string\n$length = mb_strlen($str);\necho "The length of the string is: $length\n"; // Output: 5\n\n// Find the position of a substring\n$position = mb_strpos($str, "に");\necho "The position of 'に' is: $position\n"; // Output: 2\n\n// Substring extraction\n$substring = mb_substr($str, 1, 3);\necho "The substring is: $substring\n"; // Output: んにち\n?>