<?php\nheader('Content-Type: image/png');\n\n// Create a blank image\n$image = imagecreatetruecolor(400, 100);\n\n// Set background and text color\n$bgColor = imagecolorallocate($image, 255, 255, 255);\n$textColor = imagecolorallocate($image, 0, 0, 0);\n\n// Fill the background color\nimagefilledrectangle($image, 0, 0, 400, 100, $bgColor);\n\n// Fetch the current stock price (replace this with actual data fetching logic)\n$stockPrice = "Stock Price: $" . rand(100, 500);\n\n// Add the text to the image\nimagettftext($image, 20, 0, 10, 50, $textColor, 'arial.ttf', $stockPrice);\n\n// Output the image\nimagepng($image);\nimagedestroy($image);\n?>