<?php\n$mysqli = new mysqli("host", "user", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$query = "SELECT * FROM andyasks ORDER BY date_posted ASC";\n$results = $mysqli->query($query);\n\n$questions = [];\n$currentQuestion = null;\n\nwhile ($row = $results->fetch_assoc()) {\n    if ($row['user'] == 'Andy') {\n        // This is a question\n        $currentQuestion = [\n            'question' => $row['text'],\n            'date' => $row['date_posted'],\n            'answers' => []\n        ];\n        $questions[] = &$currentQuestion;\n    } else {\n        // This is an answer\n        if ($currentQuestion !== null) {\n            $currentQuestion['answers'][] = $row['text'];\n        }\n    }\n}\n\n// Output the questions and their associated answers\nforeach ($questions as $question) {\n    echo "<p><strong>Question:</strong> {$question['question']}</p>";\n    if (!empty($question['answers'])) {\n        echo "<ul>";\n        foreach ($question['answers'] as $answer) {\n            echo "<li><strong>Answer:</strong> {$answer}</li>";\n        }\n        echo "</ul>";\n    }\n}\n\n$mysqli->close();\n?>