function get_feed() {\n    // Include the RSS fetch function\n    include_once(ABSPATH . WPINC . '/rss.php');\n\n    // Fetch the RSS feed\n    $rss = fetch_rss('http://dorar.shamekh.ws/?feed=rss2');\n\n    // Check if the RSS feed was fetched successfully\n    if (is_wp_error($rss)) {\n        return []; // Return an empty array or handle the error as needed\n    }\n\n    // Ensure that $rss->items is an array before slicing\n    if (isset($rss->items) && is_array($rss->items)) {\n        $maxitems = 1;\n        $items = array_slice($rss->items, 0, $maxitems, false);\n        return $items;\n    } else {\n        return []; // Return an empty array if $rss->items is not set or not an array\n    }\n}