<?php\n// Set headers to indicate the response will be chunked\nheader('Content-Type: text/csv');\nheader('Content-Disposition: attachment; filename="export.csv"');\nheader('Transfer-Encoding: chunked');\nheader('Connection: keep-alive');\n\n// Function to send a chunk of data\nfunction send_chunk($chunk) {\n    printf("%x\r\n", strlen($chunk));\n    echo $chunk;\n    echo "\r\n";\n    flush();\n}\n\n// Open a connection to your database\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'your_user', 'your_password');\n\n// Execute your query\n$query = $pdo->query('SELECT * FROM your_table');\n\n// Send CSV headers\nsend_chunk("Column1,Column2,Column3\n");\n\n// Fetch results and send them in chunks\nwhile ($row = $query->fetch(PDO::FETCH_ASSOC)) {\n    $chunk = "{$row['column1']},{$row['column2']},{$row['column3']}\n";\n    send_chunk($chunk);\n}\n\n// Send the final chunk\nsend_chunk("");\n?>