class AclService\n{\n    protected $acl;\n\n    public function __construct()\n    {\n        $this->acl = new Zend_Acl();\n\n        // Define roles\n        $this->acl->addRole(new Zend_Acl_Role('guest'));\n        $this->acl->addRole(new Zend_Acl_Role('user'), 'guest');\n        $this->acl->addRole(new Zend_Acl_Role('admin'), 'user');\n\n        // Define resources\n        $this->acl->add(new Zend_Acl_Resource('default'));\n\n        // Assign permissions\n        $this->acl->allow('guest', 'default', 'view');\n        $this->acl->allow('user', 'default', ['view', 'edit']);\n        $this->acl->allow('admin', 'default', null); // All permissions\n\n        // Add more roles and resources as needed\n    }\n\n    public function isAllowed($role, $resource, $privilege)\n    {\n        return $this->acl->isAllowed($role, $resource, $privilege);\n    }\n}