<?php\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n  die("Connection failed: " . $conn->connect_error);\n}\n\n// Get the current page number from the URL parameter (default to 1 if not set)\n$page = isset($_GET['page']) ? (int)$_GET['page'] : 1;\n$records_per_page = 100;\n$offset = ($page - 1) * $records_per_page;\n\n// Prepare the SQL query\n$sql = "SELECT * FROM your_table LIMIT $records_per_page OFFSET $offset";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n  // Output data of each row\n  while($row = $result->fetch_assoc()) {\n    echo "id: " . $row["id"]. " - Name: " . $row["name"]. "<br>";\n  }\n} else {\n  echo "0 results";\n}\n$conn->close();\n?>\n\n<!-- Pagination controls -->\n<?php\n// Calculate total pages\n$total_records_sql = "SELECT COUNT(*) FROM your_table";\n$total_records_result = $conn->query($total_records_sql);\n$total_records = $total_records_result->fetch_row()[0];\n$total_pages = ceil($total_records / $records_per_page);\n\nfor ($i = 1; $i <= $total_pages; $i++) {\n  echo "<a href='your_script.php?page=" . $i . "'>" . $i . "</a> ";\n}\n?>