// In your controller\npublic function saveNewsletterAsHtml() {\n    // Use requestAction to get the content\n    $content = $this->requestAction('/newsletters/view', array('return'));\n\n    // Load the layout and set the content\n    $this->set('content', $content);\n    $this->layout = 'newsletter'; // Set the desired layout\n\n    // Render the view with the full layout\n    $html = $this->render(null, 'newsletter')->body();\n\n    // Output or save the HTML as needed\n    $this->response->type('html');\n    $this->response->body($html);\n    return $this->response;\n}