require 'vendor/autoload.php';\n\nuse Google\Cloud\Vision\V1\ImageAnnotatorClient;\n\nfunction detect_safe_search($path)\n{\n    $imageAnnotator = new ImageAnnotatorClient();\n    $image = file_get_contents($path);\n\n    $response = $imageAnnotator->safeSearchDetection($image);\n    $safeSearch = $response->getSafeSearchAnnotation();\n\n    $adult = $safeSearch->getAdult();\n    $violence = $safeSearch->getViolence();\n    $racy = $safeSearch->getRacy();\n\n    $imageAnnotator->close();\n\n    return [\n        'adult' => $adult,\n        'violence' => $violence,\n        'racy' => $racy,\n    ];\n}\n\n$path = 'path/to/your/image.jpg';\n$safeSearchResult = detect_safe_search($path);\n\nif ($safeSearchResult['adult'] >= 3 || $safeSearchResult['violence'] >= 3 || $safeSearchResult['racy'] >= 3) {\n    echo "The image contains inappropriate content.";\n} else {\n    echo "The image is safe.";\n}