<?php\n// Create an in-memory SQLite database\n$db = new SQLite3(':memory:');\n\n// Define some tables\n$db->exec("CREATE TABLE table1 (id INTEGER, value TEXT)");\n$db->exec("CREATE TABLE table2 (id INTEGER, description TEXT)");\n\n// Insert data into tables\n$db->exec("INSERT INTO table1 VALUES (1, 'A'), (2, 'B'), (3, 'C')");\n$db->exec("INSERT INTO table2 VALUES (1, 'Desc1'), (2, 'Desc2'), (4, 'Desc4')");\n\n// Perform a SQL-like join operation\n$result = $db->query("SELECT * FROM table1 INNER JOIN table2 ON table1.id = table2.id");\n\nwhile ($row = $result->fetchArray(SQLITE3_ASSOC)) {\n    echo "id: {$row['id']}, value: {$row['value']}, description: {$row['description']}\n";\n}\n\n$db->close();\n?>