<?php\n// Define the file path\n$file_to_send = 'ftp_test_from_created_file.txt';\n\n// Ensure the directory has write permissions (e.g., 0777 for full permissions)\n// You may need to do this manually or via command line, not from PHP:\n// chmod /path/to/containingfolder 0777\n\n// Create and open the file for writing\n$fh = fopen($file_to_send, 'w');\nif ($fh === false) {\n    die('Failed to create file');\n}\n\n// Write data to the file\n$tsv_line = "Hey\tThese\tAre\tTab\tSeparated\tValues";\nfwrite($fh, $tsv_line);\nfwrite($fh, "\n");\n$tsv_line = "Hey\tThese\tAre\tToo";\nfwrite($fh, $tsv_line);\n\n// Close the file handle\nfclose($fh);\n\n// Set file permissions if needed\nchmod($file_to_send, 0666); // Set desired permissions, e.g., 0666 for read/write for all users\n\n?>