<?php\n\nfunction generateList($tree) {\n    $html = '<ul>';\n    foreach ($tree as $node) {\n        $html .= '<li>';\n        if (isset($node['value']) && is_array($node['value'])) {\n            foreach ($node['value'] as $child) {\n                if ($child['tag'] === 'Title') {\n                    $html .= $child['value'];\n                }\n                if (isset($child['value']) && is_array($child['value'])) {\n                    $html .= generateList([$child]);\n                }\n            }\n        }\n        $html .= '</li>';\n    }\n    $html .= '</ul>';\n    return $html;\n}\n\n$tree = array(\n    0 => array(\n        'tag' => 'NavigationMode',\n        'value' => array(\n            0 => array(\n                'tag' => 'Title',\n                'value' => 'Introduction'\n            ),\n            1 => array(\n                'tag' => 'NavigationNode',\n                'value' => array(\n                    0 => array(\n                        'tag' => 'Title',\n                        'value' => 'Sub Intro'\n                    )\n                )\n            )\n        )\n    ),\n    1 => array(\n        'tag' => 'NavigationMode',\n        'value' => array(\n            0 => array(\n                'tag' => 'Title',\n                'value' => 'Module 1'\n            )\n        )\n    )\n);\n\necho generateList($tree);\n\n?>