<?php\n// URL of the external XML file\n$xml_url = 'http://example.com/path/to/your.xml';\n\n// Load the XML content\n$xml = new DOMDocument;\n$xml->load($xml_url);\n\n// Load the XSLT file\n$xsl = new DOMDocument;\n$xsl->load('tabledisplay.xsl');\n\n// Configure the transformer\n$proc = new XSLTProcessor;\n$proc->importStyleSheet($xsl);\n\n// Transform the XML\n$html = $proc->transformToXML($xml);\n\n// Output the resulting HTML\nheader('Content-Type: text/html; charset=utf-8');\necho $html;\n?>