// Create the form element\n$selectElement = new Zend_Form_Element_Select('score');\n\n// Add options to the select element\n$selectElement->addMultiOptions(array(\n    '-3' => '-3',\n    '-2' => '-2',\n    '-1' => '-1',\n    '0' => '0',\n    '1' => '1',\n    '2' => '2',\n    '3' => '3'\n));\n\n// Get the InArray validator and set custom messages\n$inArrayValidator = $selectElement->getValidator('InArray');\n$inArrayValidator->setMessages(array(\n    Zend_Validate_InArray::NOT_IN_ARRAY => 'The selected value is not a valid option.'\n));\n\n// Add the Between validator to the element\n$betweenValidator = new Zend_Validate_Between(array('min' => -3, 'max' => 3, 'inclusive' => true));\n$betweenValidator->setMessages(array(\n    Zend_Validate_Between::NOT_BETWEEN_STRICT => 'Value must be between -3 and 3, inclusive.',\n    Zend_Validate_Between::NOT_BETWEEN => 'Value must be between -3 and 3.'\n));\n$selectElement->addValidator($betweenValidator);\n\n// Add the element to the form\n$form = new Zend_Form();\n$form->addElement($selectElement);