class TextTransformer {\n    private function transformText($text) {\n        // Perform some transformation on the text\n        return strtoupper($text);\n    }\n\n    public function applyTransformation($input) {\n        // Use an anonymous function to call the private method\n        return preg_replace_callback('/\w+/', function($matches) {\n            return $this->transformText($matches[0]);\n        }, $input);\n    }\n}\n\n$transformer = new TextTransformer();\n$result = $transformer->applyTransformation("hello world");\necho $result; // Outputs: HELLO WORLD