function removeAccents($str) {\n    $unwanted_array = array(\n        'á'=>'a', 'à'=>'a', 'â'=>'a', 'ä'=>'a', 'ã'=>'a', 'å'=>'a', 'æ'=>'ae', 'ç'=>'c', 'é'=>'e', 'è'=>'e', 'ê'=>'e', 'ë'=>'e', \n        'í'=>'i', 'ì'=>'i', 'î'=>'i', 'ï'=>'i', 'ñ'=>'n', 'ó'=>'o', 'ò'=>'o', 'ô'=>'o', 'ö'=>'o', 'õ'=>'o', 'ø'=>'o', 'ú'=>'u', \n        'ù'=>'u', 'û'=>'u', 'ü'=>'u', 'ý'=>'y', 'ÿ'=>'y', 'Á'=>'A', 'À'=>'A', 'Â'=>'A', 'Ä'=>'A', 'Ã'=>'A', 'Å'=>'A', 'Æ'=>'AE', \n        'Ç'=>'C', 'É'=>'E', 'È'=>'E', 'Ê'=>'E', 'Ë'=>'E', 'Í'=>'I', 'Ì'=>'I', 'Î'=>'I', 'Ï'=>'I', 'Ñ'=>'N', 'Ó'=>'O', 'Ò'=>'O', \n        'Ô'=>'O', 'Ö'=>'O', 'Õ'=>'O', 'Ø'=>'O', 'Ú'=>'U', 'Ù'=>'U', 'Û'=>'U', 'Ü'=>'U', 'Ý'=>'Y'\n    );\n    return strtr($str, $unwanted_array);\n}\n\n$input = "Fóø Bår";\n$output = removeAccents($input);\n\necho $output;  // Output: Foo Bar